mapper.update:
update live
identifier = #{identifier}, name = #{name}, cover = #{cover}, status = #{status}, push_add = #{pushAdd}, pull_add = #{pullAdd}, create_uid = #{createUid}, create_name = #{createName}, create_avatar = #{createAvatar}, is_deleted = #{isDeleted}, create_time = #{createTime}, update_time = #{updateTime}
where id = #{id}

update live set status = 2, update_time = now() where identifier in
#{o.StreamName}
and is_deleted = 0

update live set status = 3, update_time = now() where identifier not in
#{o.StreamName}
and is_deleted = 0 and status = 2
